import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Import axios

function LoginForm() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Make a POST request to your backend login endpoint
      const result = await axios.post("/api/login", { username, password });
      if (result.status === 200) {
        // Assuming your backend sends some success status
        console.log("Login successful:", result.data);
        navigate("/"); // Adjust to navigate to the desired route upon successful login
      } else {
        // Handle failed login attempt
        console.error("Login failed:", result.data);
        alert("Login failed: Incorrect username or password."); // Simple alert, adjust according to your UI needs
      }
    } catch (error) {
      console.error("Error during login:", error);
      alert("Login error, please try again."); // Simple error handling
    }
  };

  return (
    <div className="login-form">
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default LoginForm;
