// LoginForm.tsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios, { AxiosError } from "axios"; // Make sure axios is imported

function LoginForm() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/login", {
        username,
        password,
      });
      if (response.data.success) {
        navigate("/"); // Redirect to home on success
      } else {
        setErrorMessage("Invalid username or password"); // Show custom error message
      }
    } catch (error) {
      if (axios.isAxiosError(error)) {
        setErrorMessage(
          error?.response?.data?.message || "An error occurred during login"
        ); // Show error from response
      } else {
        setErrorMessage("An error occurred during login"); // Generic error message
      }
    }
  };

  // Styling to match the provided appearance
  return (
    <div
      className="login-form"
      style={{
        background: "black",
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <form
        onSubmit={handleSubmit}
        style={{ background: "gray", padding: "20px", borderRadius: "5px" }}
      >
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="username" style={{ color: "white" }}>
            Username:
          </label>
          <input
            id="username"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={{
              display: "block",
              width: "100%",
              padding: "10px",
              marginTop: "5px",
            }}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label htmlFor="password" style={{ color: "white" }}>
            Password:
          </label>
          <input
            id="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{
              display: "block",
              width: "100%",
              padding: "10px",
              marginTop: "5px",
            }}
          />
        </div>
        <button
          type="submit"
          style={{
            width: "100%",
            padding: "10px",
            marginTop: "10px",
            background: "blue",
            color: "white",
            border: "none",
            borderRadius: "5px",
          }}
        >
          Login
        </button>
        {errorMessage && (
          <div style={{ color: "red", marginTop: "10px" }}>{errorMessage}</div>
        )}
      </form>
    </div>
  );
}

export default LoginForm;
